// src/components/FileUpload.js
import React, { useState } from 'react';
import axios from 'axios';

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [data, setData] = useState(null);
    const [error, setError] = useState(null);

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
        setError(null); // Reset error on file change
        setData(null); // Reset data on new file selection
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('document', file);

        try {
            const response = await axios.post('http://127.0.0.1:5000/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            // Update data state with the response data
            setData(response.data);
        } catch (err) {
            setError('Failed to upload document');
            console.error(err);
        }
    };

    return (
        <div>
            <h1>Document Upload</h1>
            <form onSubmit={handleSubmit}>
                <input type="file" name="document" onChange={handleFileChange} required />
                <button type="submit">Upload</button>
            </form>

            {error && <p style={{ color: 'red' }}>{error}</p>}
            {data && (
                <div>
                    <h2>Extracted Data</h2>
                    <p><strong>Text:</strong> {data.text}</p>
                    {/* Display extracted fields if available */}
                    {data.name && <p><strong>Name:</strong> {data.name}</p>}
                    {data.documentNumber && <p><strong>Document Number:</strong> {data.documentNumber}</p>}
                    {data.expirationDate && <p><strong>Expiration Date:</strong> {data.expirationDate}</p>}
                </div>
            )}
        </div>
    );
};

export default FileUpload;
